mapper.select:
SELECT 

FROM fcl_withdraw a

WHERE a.id = #{id}

SELECT
FROM fcl_withdraw a
AND a.outnumber_id=#{outnumberId}
AND u.id LIKE '%'||#{user.id}||'%' '%'+#{user.id}+'%' CONCAT('%', #{user.id}, '%')
ORDER BY ${page.orderBy}
ORDER BY a.create_date desc

mapper.update:
UPDATE fcl_withdraw SET 	
	outnumber_id = #{outnumberId},
	money = #{money},
	remarks = #{remarks}
WHERE id = #{id}

UPDATE sys_user SET 

	wkf = wkf - #{money} - #{money}*0.1
WHERE id = #{outnumberId}
